mapper.select:
select ,detail from cskaoyan_mall_goods
1 = 1 and deleted = 0
and goods_sn = #{goodsSn}
and `name` like concat('%',#{name},'%')
order by #{sort} #{order}

mapper.update:
update cskaoyan_mall_goods
set deleted = 1
where id = #{goodId}

update cskaoyan_mall_goods_specification
set deleted = 1
where goods_id = #{goodId}

update cskaoyan_mall_goods_product
set deleted = 1
where goods_id = #{goodId}

update cskaoyan_mall_goods_attribute
set deleted = 1
where goods_id = #{goodId}

update cskaoyan_mall_goods_product
set deleted = true , update_time = CURRENT_TIMESTAMP
where goods_id = #{goodsId}

mapper.select:
select
id,value_id as valueId,type,content,user_id as userId,has_picture as hasPicture,
pic_urls as picUrls,star,add_time as addTime,update_time as updateTime,deleted
from
cskaoyan_mall_comment

  1 = 1 and deleted = 0 and `type` = 0
  
    and user_id = #{userId}
  
  
    and value_id = #{valueId}
  

order by #{sort} #{order}

mapper.update:
update cskaoyan_mall_comment
set deleted = true , update_time = CURRENT_TIMESTAMP
where id = #{comment.id}
